package com.szjm.scanner;import java.util.Scanner;public class Demo04 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        //创建一个扫描器对象 用于接收键盘数据        int i = 0;        float f = 0.0f;        System.out.println("请输入整数: ");        if (scanner.hasNextInt()){            i = scanner.nextInt();            System.out.println("整数数据: " + i);        }else {            System.out.println("你输入的不是整数");        }        System.out.println("===================================");        System.out.println("请输入小数: ");        if (scanner.hasNextFloat()){            f = scanner.nextFloat();            System.out.println("小数数据: " + f);        }else {            System.out.println("你输入的不是小数");        }        scanner.close();    }}







求和计算器package com.szjm.scanner;import java.util.Scanner;public class Demo05 {    public static void main(String[] args) {        //输入多个数字 求总和平均数 没输入一个数字用回车键确认 输入非数字键结束并输出执行结果        Scanner scanner = new Scanner(System.in);        //和        double sum = 0;        //计算输入了多少数字        int m = 0;        //每一次输入的数        double x;        System.out.println("输入需要相加的数 回车键确认 输入非数字键结束并输出执行结果");        //通过循环判断是否还有输入 并对每一次输入进行求和和统计        while (scanner.hasNextDouble()){            x = scanner.nextDouble();            m += 1;            sum += x;            System.out.println("你输入了第" + m + "个数据,当前sum = " + sum + ",当前平均数是" + (sum / m));        }        System.out.println("======================================");        System.out.println(m + "个数的总和是" + sum);        System.out.println(m + "个数的平均数是" + (sum / m));        scanner.close();    }}

