package com.szjm.struct;import java.util.Scanner;public class IfDemo03 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        System.out.println("请输入成绩: ");        int score = scanner.nextInt();        if (score == 100){            System.out.println("S");        }else if(score &lt; 100 &amp;&amp; score &gt;= 90){            System.out.println("A");        }else if(score &lt; 90 &amp;&amp; score &gt;= 80){            System.out.println("B");        }else if(score &lt; 80 &amp;&amp; score &gt;= 70){            System.out.println("C");        }else if(score &lt; 70 &amp;&amp; score &gt;= 60){            System.out.println("D");        }else if(score &lt; 60 &amp;&amp; score &gt; 0){            System.out.println("E");        }else {            System.out.println("成绩不合法");        }        scanner.close();    }}





