package com.szjm.method;import java.util.Scanner;public class Job01 {    public static void main(String[] args) {        int i = 1;        while (i == 1){            calculator();            System.out.println("输入0退出，否则继续");            Scanner scanner = new Scanner(System.in);            i = Integer.parseInt(scanner.nextLine());        }        System.out.println("退出计算器");    }    public static void calculator(){        Scanner scanner = new Scanner(System.in);        System.out.println("请输入运算符：");        String operator = scanner.nextLine();        System.out.println("请输入要计算的数字a：");        double a = Integer.parseInt(scanner.nextLine());        System.out.println("请输入要计算的数字b：");        double b = Integer.parseInt(scanner.nextLine());        switch (operator){            case "+":                System.out.print(a+ "+" + b + "=");                System.out.println(add(a, b));                break;            case "-":                System.out.print(a+ "-" + b + "=");                System.out.println(sub(a, b));                break;            case "*":                System.out.print(a+ "*" + b + "=");                System.out.println(mul(a, b));                break;            case "/":                System.out.print(a+ "/" + b + "=");                System.out.println(div(a, b));                break;        }    }    public static double add(double a, double b){        return a + b;    }    public static double sub(double a, double b){        return a - b;    }    public static double mul(double a, double b){        return a * b;    }    public static double div(double a, double b){        return a / b;    }}

